#define ll long long

class Solution {
public:

    ll solve(vector<pair<int, int>> &a, vector<pair<int, int>> &b, vector<pair<int, int>> &c)
    {
        ll ans = -4e9;
        for (int i=0;i<3;i++)
        {
            for (int j=0;j<3;j++)
            {
                for (int k=0;k<3;k++)
                {
                    if (a[i].second == b[j].second || a[i].second == c[k].second || b[j].second == c[k].second)
                        continue;
                    ll cur = a[i].first*1LL + b[j].first*1LL + c[k].first*1LL;
//                     if (cur == 3)
//                     {
//                         cout<<i<<" "<<j<<" "<<k<<endl;
//                         print(a);
//                         print(b);
//                         print(c);

//                     }
                    ans = max(ans, cur);
                }
            }
        }
        return ans;
    }

    void print(vector<pair<int, int>> &a)
    {
        for (int i=0;i<a.size();i++)
            cout<<a[i].first<<" "<<a[i].second<<endl;
        cout<<endl;
    }

    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        vector<vector<pair<int, int>>> v(n, vector<pair<int, int>>(m));
        for (int i=0;i<n;i++)
        {
            vector<pair<int, int>> tmp;
            for (int j=0;j<m;j++)
                tmp.push_back({a[i][j], j});
            sort(tmp.begin(), tmp.end());
            for (int j=0;j<m;j++)
                v[i][j] = tmp[j];
        }
        ll ans = -4e9;
        for (int r1=0;r1<=n-3;r1++)
        {
            vector<pair<int, int>> c1;
            for (int j=m-3;j<m;j++)
                c1.push_back(v[r1][j]);
            for (int r2 = r1+1;r2<=n-2;r2++)
            {
                vector<pair<int, int>> c2;
                for (int j=m-3;j<m;j++)
                    c2.push_back(v[r2][j]);
                for (int r3=r2+1;r3<=n-1;r3++)
                {
                    vector<pair<int, int>> c3;
                    for (int j=m-3;j<m;j++)
                        c3.push_back(v[r3][j]);
                    ll cur = solve(c1, c2, c3);
//                     if (cur > ans)
//                     {
//                         print(c1);
//                         print(c2);
//                         print(c3);

//                     }
                    ans = max(ans, cur);
                }
            }
        }
        return ans;
    }
};
