class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        grid = []
        for i in board:
            a = [(y, num) for num, y in enumerate(i)]
            a.sort(reverse = True)
            grid.append(a[:3])
        res = -inf
        qaq = [(0,1,2), (0,2,1), (1,0, 2), (1,2,0), (2,0,1),(2,1,0)]
        for i in range(m):
            a = grid[i]
            for j in range(i + 1, m):
                b = grid[j]
                for k in range(j + 1, m):
                    c = grid[k]
                    if i == j or i == k or j == k:
                        continue
                    for q0 in range(3):
                        for q1 in range(3):
                            for q2 in range(3):
                                x = a[q0]
                                y = b[q1]
                                z = c[q2]
                                if x[1] == y[1] or x[1] == z[1] or y[1] == z[1]:
                                    continue
                                v = x[0] + y[0] + z[0]

                                if v > res:
                                    res = v
        return res
