#define pb push_back
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& v) {
        int n = v.size();
        int m = v[0].size();
        vector<vector<int>> ma(n);
        for(int i=0;i<n;i++)
        {
            multiset<pair<int,int>> st;
            for(int j=0;j<m;j++)
            {
                if(st.size()<3)st.insert({v[i][j],j});
                else
                {
                    if((*st.begin()).first < v[i][j]) 
                    {
                        st.erase(st.begin());
                        st.insert({v[i][j],j});
                    }
                }
            }
            for(auto [a,b] : st)ma[i].pb(b);
        }
        long long int ans = -1e15;
        for(int i=0;i<n;i++)
        {
            for(int j=i+1;j<n;j++)
            {
                for(int k=j+1;k<n;k++)
                {
                    long long int curr = -1e15;
                    for(int a=0;a<3;a++)
                    {
                        for(int b=0;b<3;b++)
                        {
                            for(int c=0;c<3;c++)
                            {
                                if(!(ma[i][a]==ma[j][b] || ma[i][a]==ma[k][c] || ma[j][b]==ma[k][c]))
                                {
                                    curr=max(curr,v[i][ma[i][a]]*1ll+v[j][ma[j][b]]*1ll+v[k][ma[k][c]]*1ll);
                                }
                            }
                        }
                    }
                    ans=max(ans,curr);
                }
            }
        }
        return ans;
    }
};
