class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long  n= board.size(), m = board[0].size(),i ,j ,k ,l;
        vector<pair<long long, long long>> vec[n];
        for(i=0;i<n;i++){
            for(j=0;j<m;j++){
                vec[i].push_back({board[i][j], j});
            }
        }
        long long ans = LLONG_MIN;
        for(auto &x: vec) sort(x.begin(), x.end());
        for(i=0;i<n;i++){
            for(j=i+1;j<n;j++){
                for(k=j+1;k<n;k++){
                    for(long long  x = m-3; x<m; x++){
                        for(long long y=m-3;y<m;y++){
                            for(long long  z=m-3; z<m;z++){
                                if(vec[i][x].second!=vec[j][y].second && vec[i][x].second != vec[k][z].second && vec[j][y].second != vec[k][z].second){
                                    ans = max(ans, vec[i][x].first+vec[j][y].first+vec[k][z].first);
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;

    }
};
