class Solution {
    public long maximumValueSum(int[][] board) {
        int m = board.length, n = board[0].length;
        long ans = Long.MIN_VALUE;
        List<Integer> nums = new ArrayList<>(m*3);
        for (int i = 0; i < m; i++) {
            int max1 = -1, max2 = -1, max3 = -1;
            for (int j = 0; j < n; j++) {
                if (max1 == -1 || board[i][j] > board[max1/n][max1%n]) {
                    max3 = max2;
                    max2 = max1;
                    max1 = i*n+j;
                } else if(max2 == -1 || board[i][j] > board[max2/n][max2%n]) {
                    max3 = max2;
                    max2 = i*n+j;
                } else if (max3 == -1 || board[i][j] > board[max3/n][max3%n]) {
                    max3 = i*n+j;
                }
            }
            nums.add(max1);
            nums.add(max2);
            nums.add(max3);
        }
        for (int i = 0; i < nums.size(); i++) {
            int x1 = nums.get(i)/n, y1 = nums.get(i)%n;
            for (int j = i+1; j < nums.size(); j++) {
                int x2 = nums.get(j)/n, y2 = nums.get(j)%n;
                if (x2 == x1 || y1 == y2) {
                    continue;
                }
                for (int k = j+1; k < nums.size(); k++) {
                    int x3 = nums.get(k)/n, y3 = nums.get(k)%n;
                    if (x3 == x1 || x3 == x2 || y3 == y1 || y3 == y2) {
                        continue;
                    }
                    ans = Math.max(ans, (long)board[x1][y1] + board[x2][y2] + board[x3][y3]);
                }
            }
        }

        return ans;
    }
}
