class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();
        // vector<bool> brow(m,1), bcol(n,1);
        vector<vector<int>> rowmaxi(m);
        long long ans = -1e11;
        for(int i=0; i<m; i++)
        {
            int maxi1 = 0, maxi2 = 1, maxi3 = 2;
            if(board[i][maxi1] < board[i][maxi3]) { swap(maxi1,maxi3); }
            if(board[i][maxi1] < board[i][maxi2]) { swap(maxi1,maxi2); }
            if(board[i][maxi2] < board[i][maxi3]) { swap(maxi2,maxi3); }
            for(int j=3; j<n; j++)
            {
                if(board[i][maxi1] < board[i][j])
                {
                    maxi3 = maxi2;
                    maxi2 = maxi1;
                    maxi1 = j;
                }
                else if(board[i][maxi2] < board[i][j] )
                {
                    maxi3 = maxi2;
                    maxi2 = j;
                }
                else if(board[i][maxi3] < board[i][j] )
                {
                    maxi3 = j;
                }
            }
            rowmaxi[i] = {maxi1,maxi2,maxi3};
            // cout<<maxi1<<maxi2<<maxi3<<endl;
        }
        for(int fi=0; fi<m; fi++)
        {
            for(int fj=0; fj<3; fj++)
            {
                long long t = board[fi][rowmaxi[fi][fj]];
                for(int si=0; si<m; si++)
                {
                    if(si != fi)
                    {
                        for(int sj=0; sj<3; sj++)
                        {

                            if(rowmaxi[si][sj] != rowmaxi[fi][fj])
                            {
                                long long tt = t + board[si][rowmaxi[si][sj]];
                                for(int ti=0; ti<m; ti++)
                                {
                                    if(ti != fi && ti != si)
                                    {
                                        for(int tj =0; tj<3; tj++)
                                        {
                                            if(rowmaxi[ti][tj] != rowmaxi[fi][fj] && rowmaxi[ti][tj] != rowmaxi[si][sj])
                                            {
                                                long long ttt = tt + board[ti][rowmaxi[ti][tj]];
                                                ans = max(ans,ttt);
                                            }
                                        }
                                    }
                                } 
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
