class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<long long> >v;
        for(long long i=0;i<board.size();i++){
            for(long long j=0;j<board[0].size();j++){
                long long cur = board[i][j];
                v.push_back({cur,i,j});
            }
        }
        int sz = v.size();
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());
        long long ans = -4e9;
        for(int i=0;i<min(401,sz);i++){
            for(int j=i+1;j<min(401,sz);j++){
                if (v[i][1] == v[j][1])continue;
                if (v[i][2] == v[j][2])continue;
                for(int k=j+1;k<min(sz,401);k++){
                    if(v[i][1] == v[k][1] || v[j][1] == v[k][1])continue;
                    if(v[i][2] == v[k][2] || v[j][2] == v[k][2])continue;
                    ans = max(ans, v[i][0] + v[j][0] + v[k][0]);
                    break;
                }
            }
        }
        return ans;
    }
};
