class Solution {
public:
    long long solve(int i1, int i2, int i3, vector<vector<int>> &top3, vector<vector<int>>& board){
        auto& v1 = top3[i1];
        auto& v2 = top3[i2];
        auto& v3 = top3[i3];

        long long ans = 3LL * INT_MIN;
        for(int j1 = 0; j1 < 3; j1++){
            for(int j2 = 0; j2 < 3; j2++){
                if(v2[j2] == v1[j1])
                    continue;
                for(int j3 = 0; j3 < 3; j3++){
                    if(v3[j3] == v2[j2] || v3[j3] == v1[j1])
                        continue;
                    ans = max(ans, 0LL + board[i1][v1[j1]] + board[i2][v2[j2]] + board[i3][v3[j3]]);
                }
            }
        }
        return ans;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();
        vector<vector<int>> top3(m, vector<int>(3, -1));
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>> > max3;
        for(int i = 0; i < m; i++){
            // int mx1 = INT_MIN, mx2 = INT_MIN, mx3 = INT_MIN, mx1_id = -1, mx2_id = -1, mx3_id = -1;
            // max3.clear();
            for(int j = 0; j < n; j++){
                max3.push({board[i][j], j});
                if(max3.size() > 3)
                    max3.pop();
            }
            top3[i][2] = max3.top().second; max3.pop();
            top3[i][1] = max3.top().second; max3.pop();
            top3[i][0] = max3.top().second; max3.pop();
        }

        long long ans = 3LL * INT_MIN;
        for(int i1 = 0; i1 < m; i1++){
            for(int i2 = i1 + 1; i2 < m; i2++){
                for(int i3 = i2 + 1; i3 < m; i3++){
                    ans = max(ans, solve(i1, i2, i3, top3, board));
                }
            }
        }

        return ans;
    }
};
