class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();
        vector<vector<int>> col(m, vector<int>(3, -1));
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(col[i][0] == -1 || board[i][j] > board[i][col[i][0]]){
                    col[i][2] = col[i][1];
                    col[i][1] = col[i][0];
                    col[i][0] = j;
                }
                else if(col[i][1] == -1 || board[i][j] > board[i][col[i][1]]){
                    col[i][2] = col[i][1];
                    col[i][1] = j;
                }
                else if(col[i][2] == -1 || board[i][j] > board[i][col[i][2]]){
                    col[i][2] = j;
                }
            }
        }
        long long ans = -3e9;
        for(int i=0;i<m;i++){ // choose 0
            long long t = board[i][col[i][0]];
            for(int j=0;j<m;j++){
                if(j == i)
                    continue;
                for(int k=0;k<m;k++){
                    if(k == j || k == i)
                        continue;
                    for(int a=0;a<3;a++){
                        if(col[j][a] == col[i][0])
                            continue;
                        for(int b=0;b<3;b++){
                            if(col[k][b] == col[j][a] || col[k][b] == col[i][0])
                                continue;
                            ans = max(ans, t + board[j][col[j][a]] + board[k][col[k][b]]);
                        }
                    }
                }
            }
        }
        return ans;
    }
};
