class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& bb) {
        int n = bb.size();
        vector<vector<int>>v(n);
        int i=0;
        for(auto x:bb)
        {
            vector<vector<int>>c;

            for(int j=0;j<x.size();j++)
            {
                c.push_back({x[j],j});
            }
            sort(c.begin(),c.end(),greater<vector<int>>());
            for(int j=0;j<3;j++)
            v[i].push_back(c[j][1]);
            i++;
        }

        long long ans =LLONG_MIN;

        for(int i=0;i<v.size();i++)
        {
            for(int j=i+1;j<v.size();j++)
            {
                for(int k=j+1;k<v.size();k++)
                {
                    for(int ii=0;ii<3;ii++)
                    {
                        for(int jj=0;jj<3;jj++)
                        {
                            for(int kk=0;kk<3;kk++)
                            {
                                int a = v[i][ii];
                                int b = v[j][jj];
                                int c = v[k][kk];

                                if(a!=b&&b!=c&&a!=c)
                                {
                                  long long val =bb[i][a];
                                  val+=bb[j][b];
                                  val+=bb[k][c];
                                    ans = max(ans,val);
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;

    }
};
