class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m=board.size();
        int n=board[0].size();
        vector<vector<pair<int,int>>> values(m);
        for(int i=0;i<m;i++) {
            vector<pair<int,int>> v;
            for(int j=0;j<n;j++) {
                v.push_back({board[i][j],j});

            }
            sort(v.rbegin(),v.rend());

            values[i]=v;

        }

    long long ans=-1e12;
        for(int row1=0;row1<m-2;row1++) {
            for(int row2=row1+1;row2<m-1;row2++) {
                for(int row3=row2+1;row3<m;row3++) {



                    for(int i=0;i<3;i++) {
                            for(int j=0;j<3;j++) {
                                for(int k=0;k<3;k++) {
                                    // cout<<i<<" "<<j<<" "<<k<<'\n';
                                    // cout<<values[row1].size();
                                    int val1=values[row1][i].first;
                                    int pos1=values[row1][i].second;
                                    int val2=values[row2][j].first;
                                    int pos2=values[row2][j].second;
                                    int val3=values[row3][k].first;
                                    int pos3=values[row3][k].second;
                                    if(pos1==pos2 or pos2==pos3 or pos1==pos3) {
                                        continue;
                                    }
                                    ans=max(ans,(1LL*val1)+(1LL*val2)+(1LL*val3));
                                }
                            }
                    }





                }
            }
        }
        return ans;

    }
};
