#define pb push_back
const long long INF = 5e9;
class Solution {
public:
    long long calc(int a, int b, int c, vector<vector<pair<long long, int>>> &pref){
        long long ans=-INF;
        for(auto i : pref[a]){
            for(auto j : pref[b]){
                if(i.second==j.second) continue;
                for(auto k : pref[c]){
                    if(k.second==i.second||k.second==j.second) continue;
                    ans=max(ans, i.first+j.first+k.first);
                }
            }
        }
        return ans;
    }
    long long maximumValueSum(vector<vector<int>>& tab) {
        int n=tab.size();
        int m=tab[0].size();
        vector<vector<pair<long long, int>>> pref;
        for(int i=0;i<n;i++){
            vector<pair<long long, int>> pm;
            for(int j=0;j<m;j++){
                pm.pb({tab[i][j], j});
            }
            sort(pm.begin(), pm.end());
            vector<pair<long long, int>> temp;
            temp.pb(pm[m-1]);
            temp.pb(pm[m-2]);
            temp.pb(pm[m-3]);
            pref.pb(temp);
        }
        long long ans=-INF;
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    ans=max(ans, calc(i, j, k, pref));     
                }
            }
        }
        return ans;
    }
};
