class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:

        arr = []
        for idx, lst in enumerate(board):
            b = []
            for index, value in enumerate(lst):
                b.append((value, idx, index))
            b.sort(key = lambda x: (-x[0]))
            arr.append(b[:3])

        # print(arr)

        maximum = -float('inf')
        N = len(arr)
        for one in range(N):
            for two in range(one + 1, N):
                for three in range(two + 1, N):    
                    for i1 in range(3):
                        for i2 in range(3):
                            for i3 in range(3):   
                                if arr[one][i1][2] == arr[two][i2][2] or arr[two][i2][2] == arr[three][i3][2] or arr[three][i3][2] == arr[one][i1][2]:
                                    continue    
                                value = arr[one][i1][0] + arr[two][i2][0] + arr[three][i3][0]
                                maximum = max(maximum, value)
                    # print(maximum)

        return maximum
