class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<vector<int>> small_board(m, vector<int>(3));
        for(int i = 0;i < m;i++){
            for(int j = 0;j < n;j++){
                if(board[i][j] > board[i][small_board[i][0]]){
                    small_board[i][0] = j;
                }
            }
            if(small_board[i][0] == 0){
                small_board[i][1] = 1;
            }
            for(int j = 1;j < n;j++){
                if(board[i][j] > board[i][small_board[i][1]] && j != small_board[i][0]){
                    small_board[i][1] = j;
                }
            }
            while(small_board[i][2] == small_board[i][0] || small_board[i][2] == small_board[i][1]){
                small_board[i][2]++;
            }
            for(int j = 1;j < n;j++){
                if(board[i][j] > board[i][small_board[i][2]] && j != small_board[i][0] && j != small_board[i][1]){
                    small_board[i][2] = j;
                }
            }
        }
        long long res = -3000000000;
        for(int i = 0;i < m;i++){
            for(int j = i + 1;j < m;j++){
                for(int k = j + 1;k < m;k++){
                    for(int a: small_board[i]){
                        for(int b: small_board[j]){
                            for(int c: small_board[k]){
                                if(a != b && b != c && a != c && long(board[i][a]) + board[j][b] + board[k][c] > res){
                                    res = long(board[i][a]) + board[j][b] + board[k][c];
                                }
                            }
                        }
                    }
                }
            }
        }
        return res;
    }
};
