typedef long long ll;
const int inf = 1e9+1;

class Solution {
public:
    ll maximumValueSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();

        vector<vector<int>> mx(n, vector<int>(3, -inf)), mxi(n, vector<int>(3, -1));
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (a[i][j] >= mx[i][0]) {
                    mx[i][2] = mx[i][1], mxi[i][2] = mxi[i][1];
                    mx[i][1] = mx[i][0], mxi[i][1] = mxi[i][0];
                    mx[i][0] = a[i][j], mxi[i][0] = j;
                } else if (a[i][j] >= mx[i][1]) {
                    mx[i][2] = mx[i][1], mxi[i][2] = mxi[i][1];
                    mx[i][1] = a[i][j], mxi[i][1] = j;
                } else if (a[i][j] > mx[i][2]) {
                    mx[i][2] = a[i][j], mxi[i][2] = j;
                }
            }
            // cout << mx[i][0] << ' ' << mx[i][1] << ' ' << mx[i][2] << '\n';
        }

        ll ans = -3ll * inf;
        for (int i = 0; i < n; i++) {
            for (int j = i+1; j < n; j++) {
                for (int k = j+1; k < n; k++) {
                    for (int ii = 0; ii < 3; ii++) {
                        for (int jj = 0; jj < 3; jj++) {
                            for (int kk = 0; kk < 3; kk++) {
                                if (mxi[i][ii] == mxi[j][jj] || mxi[j][jj] == mxi[k][kk] || mxi[k][kk] == mxi[i][ii]) continue;
                                ans = max(ans, (ll)mx[i][ii] + mx[j][jj] + mx[k][kk]);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
