class Solution {
    public long maximumValueSum(int[][] board) {
        List<int[][]> list = new ArrayList<>();
        for(int[] arr : board){
            int[][] temp = new int[arr.length][2];
            for(int i =0;i<arr.length;i++){
                temp[i][0] = arr[i];
                temp[i][1] = i;
            }
            Arrays.sort(temp,(a,b)->a[0]-b[0]);
            list.add(temp);
        }
        long[][] arr = new long[list.size()*3][3];
        int i =0;
        for(int[][] temp : list){
            for(int j =temp.length-1;j>=temp.length-3;j--){
                arr[i][0] = temp[j][0];
                arr[i][1] = temp[j][1];
                arr[i][2] = i/3;
                i++;
            }
        }
        // for(long[] temp : arr){
        //     System.out.println(Arrays.toString(temp));
        // }
        long ans = Long.MIN_VALUE;
        for(i=0;i<arr.length;i++){
            for(int j =i+1;j<arr.length;j++){
                if(arr[j][1] == arr[i][1] || arr[j][2] == arr[i][2]){
                    continue;
                }
                for(int k =j+1;k<arr.length;k++){
                    if(arr[k][1]!= arr[j][1] && arr[k][1]!=arr[i][1]
                    && arr[k][2]!= arr[j][2] && arr[k][2]!=arr[i][2]){
                        ans = Math.max(ans,arr[i][0]+arr[j][0]+arr[k][0]);
                    }
                }
            }
        }
        return ans;
    }
}
