class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n=a.size(),m=a[0].size();
        vector<vector<pair<long long,int>>> t3(n);
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(t3[i].size()<3){
                    t3[i].push_back({a[i][j],j});
                    if(t3[i].size()==3){
                        if(t3[i][1].first<t3[i][0].first)swap(t3[i][1],t3[i][0]);
                        if(t3[i][2].first<t3[i][1].first)swap(t3[i][2],t3[i][1]);
                        if(t3[i][1].first<t3[i][0].first)swap(t3[i][1],t3[i][0]);
                    }
                    continue;
                }
                if(t3[i][0].first<a[i][j]){
                    t3[i][0]={a[i][j],j};
                    if(t3[i][1].first<t3[i][0].first)swap(t3[i][1],t3[i][0]);
                    if(t3[i][2].first<t3[i][1].first)swap(t3[i][2],t3[i][1]);
                    if(t3[i][1].first<t3[i][0].first)swap(t3[i][1],t3[i][0]);
                }
            }
        }
        long long ans=-1e10;
        for(int i=0;i<n;i++){
            for(int ci=0;ci<3;ci++){
                for(int j=i+1;j<n;j++){
                    for(int cj=0;cj<3;cj++){
                        if(t3[i][ci].second==t3[j][cj].second)continue;
                        for(int k=j+1;k<n;k++){
                            if((t3[i][ci].second!=t3[k][2].second) && (t3[j][cj].second!=t3[k][2].second)) ans=max(ans,t3[i][ci].first+t3[j][cj].first+t3[k][2].first);
                            else if((t3[i][ci].second!=t3[k][1].second) && (t3[j][cj].second!=t3[k][1].second)) ans=max(ans,t3[i][ci].first+t3[j][cj].first+t3[k][1].first);
                            else  ans=max(ans,t3[i][ci].first+t3[j][cj].first+t3[k][0].first);

                            // if((t3[i][ci].second!=t3[k][2].second) && (t3[j][cj].second!=t3[k][2].second)) cout << t3[i][ci].second << " " << t3[j][cj].second << " " << t3[k][2].second << endl;
                            // else if((t3[i][ci].second!=t3[k][1].second) && (t3[j][cj].second!=t3[k][1].second)) cout << t3[i][ci].second << " " << t3[j][cj].second << " " << t3[k][2].second << endl;
                            // else  cout << t3[i][ci].second << " " << t3[j][cj].second << " " << t3[k][2].second << endl;
                        }   
                    }
                }
            }
        }
        return ans;
    }
};
