class Solution {
public:
    long long cur(vector<vector<pair<int, int>>>& vp, int i, int j, int k) {
        long long temp = -1e10;
        for(int a = 0; a < 3; a++) {
            for(int b = 0; b < 3; b++) {
                if(vp[i][a].second == vp[j][b].second)
                    continue;
                for(int c = 0; c < 3; c++) {
                    if(vp[i][a].second == vp[k][c].second || vp[j][b].second == vp[k][c].second)
                        continue;
                    temp = max(temp, (long long)vp[i][a].first+vp[j][b].first+vp[k][c].first);
                }
            }
        }
        return temp;
    }
    long long maximumValueSum(vector<vector<int>>& b) {
        int m = b.size(), n = b[0].size();
        vector<vector<pair<int, int>>> vp(m);
        for(int i = 0; i < m; i++) {
            for(int i1 = 0; i1 < n; i1++)
                vp[i].push_back({b[i][i1], i1});
            sort(vp[i].rbegin(), vp[i].rend());
        }

        long long ans = -1e10;
        for(int i = 0; i < m; i++) {
            for(int j = i+1; j < m; j++) {
                for(int k = j+1; k < m; k++) {
                    ans = max(ans, cur(vp, i, j, k));
                }
            }
        }
        return ans;
    }
};
