class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        a = []
        for i, row in enumerate(board):
            for j, x in enumerate(row):
                a.append((x, i, j))
        a.sort(reverse=True)
        ans = -inf
        n = len(a)
        for i in range(n):
            s, x, y = a[i]
            if s * 3 <= ans:
                return ans
            for j in range(i + 1, n):
                b, xx, yy = a[j]
                if xx == x or yy == y:
                    continue
                ns = s + b
                for k in range(j + 1, n):
                    c, xxx, yyy = a[k]
                    if xxx == xx or xxx == x or yyy == yy or yyy == y:
                        continue
                    ns += c
                    ans = max(ans, ns)
                    break
        return ans
