typedef long long int ll; 
typedef unsigned long long ull;
typedef long double ld;       
#define pb push_back
#define pob pop_back
#define ff first
#define ss second
#define all(v) (v).begin(), (v).end()
#define sare(v) (v).begin(), (v).end()
#define MOD 1000000007

class Solution {
public:

    long long func(vector<pair<ll,ll>> &a,vector<pair<ll,ll>> &b, vector<pair<ll,ll>> &c )
    {
         ll max_sum = -1e18;

    // Iterate through all possible combinations of elements from a, b, and c
    for (int i = 0; i < 3; ++i) {
        for (int j = 0; j < 3; ++j) {
            if (a[i].second == b[j].second) continue; // Ensure different rows for a and b
            for (int k = 0; k < 3; ++k) {
                if (a[i].second == c[k].second || b[j].second == c[k].second) continue; // Ensure different rows for a, b, and c

                // Calculate the sum of the selected values
                ll current_sum = a[i].first + b[j].first + c[k].first;

                // Update max_sum if this is the highest sum found
                max_sum = max(max_sum, current_sum);
            }
        }
    }

    return max_sum;
    }





    long long maximumValueSum(vector<vector<int>>& g) {
        map<ll, vector<pair<ll,ll>> > mp;
        int n=g.size();
        int m=g[0].size();
        for(int j=0;j<m;j++)
        {
            pair<ll,ll> p;
            priority_queue < pair<ll,ll> > pq;
            for(int i=0;i<n;i++)
            {
                pq.push({g[i][j],i});
            }
            int req=3;
            while(req--)
            {
                mp[j].pb(pq.top());
                pq.pop();
            }

        }
        // for(auto i:mp)
        // {
        //     for(auto j:i.ss) cout<<j.ff<<" "<<j.ss<<" | "; cout<<endl;
        // }
        // cout<<endl;
        ll ans=-1e18;
        for(int j=0;j<m;j++)
        {
            for(int i=0;i<m;i++)
            {
                if(i==j) continue;
                for(int k=0;k<m;k++)
                {
                    if(i==k||j==k) continue;
                    ans=max(ans,func(mp[i],mp[j],mp[k]));
                }
            }
        }
        return ans;
    }
};
