class Solution {
    public long maximumValueSum(int[][] board) {
        int m = board.length, n = board[0].length;
        PriorityQueue<int[]>[] ma = new PriorityQueue[m];
        Arrays.setAll(ma, e -> new PriorityQueue<int[]>((a, b) -> a[1] - b[1]));
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                ma[i].add(new int[]{j, board[i][j]});
                if (ma[i].size() > 3) {
                    ma[i].poll();
                }
            }
        }
        long res = (long)-2000_000_000 - 1000_000_001;
        for (int i = 0; i < m; i++) {
            for (int j = i + 1; j < m; j++) {
                for (int k = j + 1; k < m; k++) {
                    res = Math.max(res, f(ma, i, j, k));
                }
            }
        }
        return res;
    }
    public long f(PriorityQueue<int[]>[] ma, int x, int y, int z) {
        PriorityQueue<int[]> p1 = ma[x], p2 = ma[y], p3 = ma[z];
        int[][] a1 = new int[3][2];
        int[][] b1 = new int[3][2];
        int[][] c1 = new int[3][2];
        for (int i = 2; i >= 0; i--) {
            a1[i] = p1.poll();
        }
        for (int i = 2; i >= 0; i--) {
            b1[i] = p2.poll();
        }
        for (int i = 2; i >= 0; i--) {
            c1[i] = p3.poll();
        }
        long res = Long.MIN_VALUE / 2;
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                if (a1[i][0] == b1[j][0]) {
                    continue;
                }
                for (int k = 0; k < 3; k++) {
                    if (c1[k][0] == b1[j][0]) {
                        continue;
                    }
                    if (c1[k][0] == a1[i][0]) {
                        continue;
                    }
                    res = Math.max(res, (long)a1[i][1] + b1[j][1] + c1[k][1]);
                }
            }
        }
        for (int i = 2; i >= 0; i--) {
            p1.add(a1[i]);
            p2.add(b1[i]);
            p3.add(c1[i]);
        }
        return res;
    }
}
