class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& bd) {
        int n = bd.size();
        int m = bd[0].size();
        vector<vector<int> > v(n, vector<int> (3,INT_MIN));

        for(int i=0;i<n;i++){
            if(bd[i][0]>bd[i][1]){
                if(bd[i][1]>bd[i][2]){
                    v[i][0]=0;v[i][1]=1;v[i][2]=2;
                }
                else if(bd[i][0]>bd[i][2]){
                    v[i][0]=0;v[i][1]=2;v[i][2]=1;
                }
                else{
                    v[i][0]=2;
                    v[i][1]=0;
                    v[i][2]=1;
                }
            }
            else{
                if(bd[i][0]>bd[i][2]){
                    v[i][0]=1;v[i][1]=0;v[i][2]=2;
                }
                else if(bd[i][1]>bd[i][2]){
                    v[i][0]=1;v[i][1]=2;v[i][2]=0;
                }
                else{v[i][0]=2;v[i][1]=1;v[i][2]=0;}
            }
            for(int j=3;j<m;j++){
                if(bd[i][j] > bd[i][v[i][0]]){
                    v[i][2]=v[i][1];
                    v[i][1]=v[i][0];
                    v[i][0]=j;
                }
                else if(bd[i][j]>bd[i][v[i][1]]){
                    v[i][2]=v[i][1];
                    v[i][1]=j;
                }
                else if(bd[i][j]>bd[i][v[i][2]]) v[i][2]=j;
            }
        }
        long long ans=LLONG_MIN;
        for(int i=2;i<n;i++){
            for(int x=0;x<3;x++){
                for(int j=1;j<i;j++){
                    for(int y=0;y<3;y++){
                        if(v[i][x]==v[j][y])continue;
                        for(int k=0;k<j;k++){
                            for(int z=0;z<3;z++){
                                if(v[i][x]!=v[j][y] && v[i][x]!=v[k][z] && v[j][y]!=v[k][z]){
                                    long long an = bd[i][v[i][x]];
                                    an += bd[j][v[j][y]];
                                    an += bd[k][v[k][z]];
                                    ans = max(ans,an);
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
