class Solution:
  def maximumValueSum(self, board: List[List[int]]) -> int:
    rows = []
    for row in board:
      rows.append(sorted([(x, j) for j, x in enumerate(row)], reverse=True)[:3])
    n = len(rows)
    ans = -inf
    res = []
    vis = set()
    def f(i):
      if i == n:
        return
      nonlocal ans
      for x, j in rows[i]:
        if j not in vis:
          if len(vis) == 2:
            ans = max(ans, sum(res) + x)
            continue
          res.append(x)
          vis.add(j)
          f(i + 1)
          res.pop()
          vis.remove(j)
      f(i + 1)
    f(0)
    return ans
