class Solution {
public:
    static bool myComp(vector<int> a, vector<int> b){
        return a[0]>b[0];
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<vector<int>>> helper(n, vector<vector<int>>(m, vector<int>(2,0)));
        for(int i=0; i<n; ++i){
            for(int j=0; j<m; ++j){
                helper[i][j][0] = board[i][j];
                helper[i][j][1] = j;
            }
        }
        for(int i=0; i<n; ++i){
            sort(helper[i].begin(), helper[i].end(), myComp);
        }

        long long ans=LONG_MIN;
        long long cand;

        for(int i0=0; i0<n; ++i0){
            for(int j0=0; j0<3; ++j0){

                for(int i1=i0+1; i1<n; ++i1){
                    for(int j1=0; j1<3; ++j1){
                        if(helper[i0][j0][1]!=helper[i1][j1][1]){

                            for(int i2=i1+1; i2<n; ++i2){
                                for(int j2=0; j2<3; ++j2){
                                    if(helper[i0][j0][1]!=helper[i2][j2][1]){
                                        if(helper[i1][j1][1]!=helper[i2][j2][1]){
                                            ans = max(ans, (long long) helper[i0][j0][0]+helper[i1][j1][0]+helper[i2][j2][0]);
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        return ans;

    }
};
