class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();

        vector<vector<pair<int, int>>> rows(m);
        for (int i = 0; i < m; i++)
        {
            vector<pair<int, int>> tmp;
            for (int j = 0; j < n; j++)
            {
                tmp.push_back({board[i][j], j});
            }
            sort(tmp.rbegin(), tmp.rend());

            for (int k = 0; k<3; k++)
            {
                rows[i].push_back(tmp[k]);
            }
        }

        vector<vector<pair<int, int>>> cols(n);
        for (int j = 0; j < n; j++)
        {
            vector<pair<int, int>> tmp;
            for (int i = 0; i < m; i++)
            {
                tmp.push_back({board[i][j], i});
            }
            sort(tmp.rbegin(), tmp.rend());
            for (int k = 0; k < 3; k++) {
                cols[j].push_back(tmp[k]);
            }
        }

        long long ans = LLONG_MIN;

        for (int r1 = 0; r1 < m; r1++)
        {
            for (auto it : rows[r1])
            {   
                long long v1 = it.first;
                int c1 = it.second;
                for (int r2 = r1 + 1; r2 < m; r2++)
                {
                    for (auto it2 : rows[r2])
                    {   
                        long long v2 = it2.first;
                        int c2 = it2.second;
                        if (c2 == c1) continue;
                        for (int r3 = r2 + 1; r3 < m; r3++)
                        {
                            for (auto it3 : rows[r3])
                            {   
                                long long v3 = it3.first;
                                int c3 = it3.second;
                                if (c3 == c1 || c3 == c2) continue;
                                long long sm = v1 + v2 + v3;
                                ans = max(ans, sm);
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
