pair<long long, long long> getMax(vector<pair<long long, long long>> &a, long long j1, long long j2){
    for(int i=0; i<a.size(); i++){
        if (a[i].second==j1 || a[i].second==j2) continue;
        return a[i];        
    }
    return {INT_MIN, INT_MIN};
}



class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long n = board.size(), m = board[0].size();
        vector<vector<pair<long long, long long>>> b(n, vector<pair<long long, long long>> (m));
        for(int i=0; i<n; i++) for(int j=0; j<m; j++) b[i][j] = {board[i][j], j};
        long long ans = (long long)INT_MIN*4;
        for(int i=0; i<n; i++){
            sort(b[i].begin(), b[i].end(), greater<pair<long long, long long>>());
        }

        // 1, 2, 3
        for(int i1=0; i1<n-2; i1++){
            long long val = b[i1][0].first, j1 = b[i1][0].second;
            for(int i2=i1+1; i2<n-1; i2++){
                auto itr = getMax(b[i2], j1, -1);
                long long val2 = itr.first, j2 = itr.second;

                for(int i3=i2+1; i3<n; i3++){
                    itr = getMax(b[i3], j1, j2);
                    long long val3 = itr.first;

                    ans = max(ans, val+val2+val3);

                }
            }
        }


        // 1, 3, 2
        for(int i1=0; i1<n-2; i1++){
            long long val = b[i1][0].first, j1 = b[i1][0].second;
            for(int i2=i1+1; i2<n-1; i2++){

                for(int i3=i2+1; i3<n; i3++){
                    auto itr = getMax(b[i3], j1, -1);
                    long long val3 = itr.first, j3 = itr.second;

                    itr = getMax(b[i2], j1, j3);
                    long long val2 = itr.first, j2 = itr.second;


                    ans = max(ans, val+val2+val3);

                }
            }
        }      


        // 2, 1, 3

        for(int i1=0; i1<n-2; i1++){

            for(int i2=i1+1; i2<n-1; i2++){

                for(int i3=i2+1; i3<n; i3++){
                    auto itr = getMax(b[i2], -1, -1);
                    long long val2 = itr.first, j2 = itr.second;


                    itr = getMax(b[i1], j2, -1);
                    long long val = itr.first, j1 = itr.second;


                    itr = getMax(b[i3], j1, j2);
                    long long val3 = itr.first, j3 = itr.second;

                    ans = max(ans, val+val2+val3);

                }
            }
        }    


        // 2, 3, 1
        for(int i1=0; i1<n-2; i1++){

            for(int i2=i1+1; i2<n-1; i2++){

                for(int i3=i2+1; i3<n; i3++){
                    auto itr = getMax(b[i2], -1, -1);
                    long long val2 = itr.first, j2 = itr.second;

                    itr = getMax(b[i3], j2, -1);
                    long long val3 = itr.first, j3 = itr.second;

                    itr = getMax(b[i1], j2, j3);
                    long long val = itr.first, j1 = itr.second;

                    ans = max(ans, val+val2+val3);

                }
            }
        }

        // 3, 1, 2

        for(int i1=0; i1<n-2; i1++){

            for(int i2=i1+1; i2<n-1; i2++){

                for(int i3=i2+1; i3<n; i3++){
                    auto itr = getMax(b[i3], -1, -1);
                    long long val3 = itr.first, j3 = itr.second;

                    itr = getMax(b[i1], -1, j3);
                    long long val = itr.first, j1 = itr.second;

                    itr = getMax(b[i2], j1, j3);
                    long long val2 = itr.first, j2 = itr.second;

                    ans = max(ans, val+val2+val3);

                }
            }
        }

        // 3, 2, 1

        for(int i1=0; i1<n-2; i1++){

            for(int i2=i1+1; i2<n-1; i2++){

                for(int i3=i2+1; i3<n; i3++){
                    auto itr = getMax(b[i3], -1, -1);
                    long long val3 = itr.first, j3 = itr.second;

                    itr = getMax(b[i2], -1, j3);
                    long long val2 = itr.first, j2 = itr.second;

                    itr = getMax(b[i1], j2, j3);
                    long long val = itr.first, j1 = itr.second;

                    ans = max(ans, val+val2+val3);

                }
            }
        }



        return ans;
    }
};
