class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long int m = board.size(), n = board[0].size();
        vector <vector <pair <long long int, long long int> > > a(n);
        for (long long int i=0; i<m; i++){
            for (long long int j=0; j<n; j++){
                a[j].push_back({board[i][j], i});
            }
        }
        for (long long int i=0; i<n; i++) sort(a[i].rbegin(), a[i].rend());
        long long int ans = -1e15;
        for (long long int i=0; i<n; i++){
            for (long long int j=i+1; j<n; j++){
                for (long long int k=j+1; k<n; k++){
                    for (long long int x=0; x<3; x++){
                        for (long long int y=0; y<3; y++){
                            for (long long int z=0; z<3; z++){
                                if (a[i][x].second != a[j][y].second &&
                                    a[j][y].second != a[k][z].second && 
                                    a[k][z].second != a[i][x].second){
                                        ans = max(ans, a[i][x].first + a[j][y].first + a[k][z].first);
                                    }
                            }
                        }
                    }
                }
            }
        }
        return ans;

    }
};
