class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        // keep top 3 values of each row.
        int n = board.size();
        int m = board[0].size();
        vector<vector<pair<int,int>>> v(n);
        for (int i = 0; i < n; i++) {
            vector<pair<int,int>> v1;

            for(int j = 0; j < m; j++) {
                v1.push_back(make_pair(board[i][j], j));
            }

            sort(v1.begin(), v1.end());
            reverse(v1.begin(), v1.end());

            v[i].push_back(v1[0]);
            v[i].push_back(v1[1]);
            v[i].push_back(v1[2]);

        }

        long long ans = -3000000000;

        for (int i = 0; i < n; i++) {
            for (int j = (i+1); j < n; j++) {
                for (int k = (j+1); k < n; k++) {

                    // i, j, k

                    for (int aa = 0; aa < 3; aa++) {
                        for (int bb = 0; bb < 3; bb++) {
                            for (int cc = 0; cc < 3; cc++) {

                                // check if legal

                                if (v[i][aa].second == v[j][bb].second || v[i][aa].second == v[k][cc].second || v[k][cc].second == v[j][bb].second) {
                                    continue;
                                }

                                ans = max(ans, (long long)v[i][aa].first + v[j][bb].first + v[k][cc].first);

                            }
                        }
                    }


                }
            }
        }
        return ans;

    }
};
