class Solution {
public:

    long long func(int ind, vector<vector<pair<long long,int>>>& v, int num1, int num2, vector<vector<vector<long long>>>& dp)
    {
        if(ind >= v.size()) return -1e12;

        if(dp[ind][num1][num2] != -1) return dp[ind][num1][num2];
        long long case1 = -1e12;
        long long case2 = -1e12;
        long long case3 = -1e12;
        long long case4 = -1e12;
        if(num1 == 100 && num2 == 100)
        {
            case1 = v[ind][0].first + func(ind+1, v, v[ind][0].second, 100, dp);
            case2 = v[ind][1].first + func(ind+1, v, v[ind][1].second, 100, dp);
            case3 = v[ind][2].first + func(ind+1, v, v[ind][2].second, 100, dp);
            case4 = func(ind+1, v, 100, 100, dp);

            return dp[ind][num1][num2] = max({case1, case2, case3, case4});

        }
        else if(num2 == 100)
        {
            if(num1 != v[ind][0].second) case1 = v[ind][0].first + func(ind+1, v, num1, v[ind][0].second, dp);
            if(num1 != v[ind][1].second) case2 = v[ind][1].first + func(ind+1, v, num1, v[ind][1].second, dp);
            if(num1 != v[ind][2].second) case3 = v[ind][2].first + func(ind+1, v, num1, v[ind][2].second, dp);
            case4 = func(ind+1, v, num1, 100, dp);

            return dp[ind][num1][num2] = max({case1, case2, case3, case4});
        }
        else
        {
            if(num1 != v[ind][0].second && num2 != v[ind][0].second) case1 = v[ind][0].first;
            if(num1 != v[ind][1].second && num2 != v[ind][1].second) case2 = v[ind][1].first;
            if(num1 != v[ind][2].second && num2 != v[ind][2].second) case3 = v[ind][2].first;
            case4 = func(ind+1, v, num1, num2, dp);

            return dp[ind][num1][num2] = max({case1, case2, case3, case4});
        }
    }


    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<vector<long long>>> dp(n+1, vector<vector<long long>> (101, vector<long long> (101, -1)));
        vector<vector<pair<long long,int>>> v;
        for(int i=0; i<n; i++)
        {
            vector<pair<long long,int>> p;
            for(int j = 0; j<m; j++)
            {
                p.push_back({board[i][j], j});
            }
            sort(p.rbegin(), p.rend());
            vector<pair<long long,int>> temp = {p[0], p[1], p[2]};
            v.push_back(temp);
        }
        set<int> taken;
        long long ans = func(0, v, 100, 100, dp);
        return ans;







    }
};
