class Solution {
public:
long long cache[105][105][105];
    long long solve(vector<vector<int>>& board,int i,int first=0,int second=0)
    {
        if(i==board.size())
        {
            return LLONG_MIN;
        }
        if(cache[i][first][second]!=-1)
        {
            return cache[i][first][second];
        }
        long long res=LLONG_MIN;
        if(first==0)
        {
            res=solve(board,i+1,0,0);
            for(int j=0;j<board[0].size();j++)
            {
                long long t=solve(board,i+1,j+1,0);
                if(t!=LLONG_MIN)
                {
                res=max(res,board[i][j]+t);     
                }

            }
        }else if(second==0)
        {
             res=solve(board,i+1,first,0);
            for(int j=0;j<board[0].size();j++)
            {
                if(j==first-1)
                {
                    continue;
                }
                long long t=solve(board,i+1,first,j+1);
                if(t!=LLONG_MIN)
                {
                res=max(res,board[i][j]+t);
                }
            }           
        }else{
            res=solve(board,i+1,first,second);
            for(int j=0;j<board[0].size();j++)
            {
                if(j==first-1 || j==second-1)
                {
                    continue;
                }
                res=max(res,(long long)board[i][j]);
            }
        }
        return cache[i][first][second]=res;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        for(int i=0;i<105;i++)
        {
            for(int j=0;j<105;j++)
            {
                for(int k=0;k<105;k++)
                {
                    cache[i][j][k]=-1;
                }
            }
        }
        return solve(board,0);
    }
};
