import heapq 

class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m = len(board)
        n = len(board[0])

        # 对每一行，获取三个最大值及其对应的列
        max_in_rows = []
        for i in range(m):
            max_vals = heapq.nlargest(3, [(board[i][j], j) for j in range(n)])
            max_in_rows.append(max_vals)

        max_sum = float('-inf')

        # 枚举三行的组合
        for r1 in range(m):
            for r2 in range(r1 + 1, m):
                for r3 in range(r2 + 1, m):
                    # 对三行分别取列索引不冲突的三个最大值
                    for v1, c1 in max_in_rows[r1]:
                        for v2, c2 in max_in_rows[r2]:
                            if c2 == c1:
                                continue
                            for v3, c3 in max_in_rows[r3]:
                                if c3 == c1 or c3 == c2:
                                    continue
                                # 计算当前和
                                current_sum = v1 + v2 + v3
                                # 更新最大值
                                max_sum = max(max_sum, current_sum)

        return max_sum
