class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();

        // find top 4 in each row
        vector<vector<int>> maxis(m);
        priority_queue<pair<int,int>> pq;

        for(int i = 0; i < m; ++i){
            for(int j = 0; j< n; ++j){
                pq.push({board[i][j], j});
            }
            while(!pq.empty()){
                auto [val, col] = pq.top();
                pq.pop();
                if(maxis[i].size() < 3) maxis[i].push_back(col);
            }
        }

        long long ans = -1e18;

        for(int row1 = 0; row1 < m; ++row1){
            for(auto col1 : maxis[row1]){

                // second rook
                for(int row2 = 0; row2 < m; ++row2){
                    if(row1 != row2){
                        for(auto &col2 : maxis[row2]){
                            if(col2 != col1){

                                // third rook
                                for(int row3 = 0; row3 < m; ++row3){
                                    if(row3 != row1 && row3 != row2){
                                        for(auto &col3 : maxis[row3]){
                                            if(col1 != col3 && col2 != col3){

                                                ans = max(ans, (long long) board[row1][col1] + board[row2][col2] + board[row3][col3]);

                                            }
                                        }
                                    }
                                }

                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
