class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<vector<pair<int, int>>> arr(m);
        for (int i = 0; i < m; i++) {
            vector<pair<int, int>> cnt;
            for (int j = 0; j < n; j++) {
                cnt.emplace_back(board[i][j], j);
            }
            sort(cnt.begin(), cnt.end());
            for (int j = n - 1; j >= n - 3; j--) {
                arr[i].emplace_back(cnt[j]);
            }
        }

        long long res = LLONG_MIN;
        for (int i = 0; i < m; i++) {
            for (int j = i + 1; j < m; j++) {
                for (int k = j + 1; k < m; k++) {
                    long long tot = 0;
                    for (int a = 0; a < 3; a++) {
                        for (int b = 0; b < 3; b++) {
                            if (arr[i][a].second == arr[j][b].second) continue;
                            for (int c = 0; c < 3; c++) {
                                if (arr[k][c].second == arr[i][a].second || arr[k][c].second == arr[j][b].second) continue;
                                long long tot = (long long)arr[i][a].first + (long long)arr[j][b].first + (long long)arr[k][c].first;
                                //cout<<arr[i][a].second<<":"<<arr[j][b].second<<":"<<arr[k][c].second<<endl;
                                //cout<<tot<<endl;
                                res = max(res, tot);
                            }
                        }
                    }
                }
            }
        }
        return res;
    }
};
