class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();

        vector<vector<pair<long long, int>>> rowTop3(m, vector<pair<long long, int>>(3, {INT_MIN, -1}));
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                if (board[i][j] > rowTop3[i][0].first) {
                    rowTop3[i][2] = rowTop3[i][1];
                    rowTop3[i][1] = rowTop3[i][0];
                    rowTop3[i][0] = {board[i][j], j};
                } else if (board[i][j] > rowTop3[i][1].first) {
                    rowTop3[i][2] = rowTop3[i][1];
                    rowTop3[i][1] = {board[i][j], j};
                } else if (board[i][j] > rowTop3[i][2].first) {
                    rowTop3[i][2] = {board[i][j], j};
                }
            }
        }

        long long maxSum = LONG_MIN;

        for (int i = 0; i < m - 2; ++i) {
            for (int j = i + 1; j < m - 1; ++j) {
                for (int k = j + 1; k < m; ++k) {
                    vector<long long> sums;
                    for (int x = 0; x < 3; ++x) {
                        for (int y = 0; y < 3; ++y) {
                            for (int z = 0; z < 3; ++z) {
                                if (rowTop3[i][x].second != rowTop3[j][y].second && 
                                    rowTop3[i][x].second != rowTop3[k][z].second && 
                                    rowTop3[j][y].second != rowTop3[k][z].second) {
                                    sums.push_back(rowTop3[i][x].first + rowTop3[j][y].first + rowTop3[k][z].first);
                                }
                            }
                        }
                    }
                    for (long long sum : sums) {
                        maxSum = max(maxSum, sum);
                    }
                }
            }
        }
        return maxSum;
    }
};
