class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        row_maximums = []
        col_maximums = []
        for row in board:
            newarr = [(row[i],i) for i in range(len(row))]
            newarr.sort(reverse=True)
            row_maximums.append(newarr[:3])
        for i in range(len(board[0])):
            newarr = [(board[j][i],j) for j in range(len(board))]
            newarr.sort(reverse=True)
            col_maximums.append(newarr[:3])
        max_sum = -float("inf")
        usedidxes = set()
        for i in range(len(board)):
            for j in range(i+1,len(board)):
                for k in range(j+1,len(board)):
                    for idx  in range(3):
                        for idx2 in range(3):
                            for idx3 in range(3):
                                if row_maximums[i][idx][1] != row_maximums[j][idx2][1] and row_maximums[i][idx][1] != row_maximums[k][idx3][1] and row_maximums[j][idx2][1] != row_maximums[k][idx3][1]:
                                    max_sum = max(max_sum,row_maximums[i][idx][0] + row_maximums[j][idx2][0] + row_maximums[k][idx3][0])
        return max_sum
