class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<pair<int,int>>> store(n+1);
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                store[i].push_back({board[i][j], j});
            }
        }
        for(auto &x : store){
            sort(x.rbegin(),x.rend());
        }
        set<int> st;
        long long ans = 1ll*board[0][0] + board[1][1] + board[2][2];
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    long long val = 0;
                    for(int h1=0;h1<3;h1++){
                        for(int h2=0;h2<3;h2++){
                            for(int h3=0;h3<3;h3++){
                                if(store[i][h1].second != store[j][h2].second && store[k][h3].second != store[j][h2].second && store[i][h1].second != store[k][h3].second){

                                ans = max(ans, 1ll*store[i][h1].first + 1ll*store[j][h2].first + store[k][h3].first);
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
