#include<bits/stdc++.h>
using namespace std;

#define ll              long long
#define ld              double
#define pb              push_back
#define mp              make_pair
#define ff              first
#define se              second
#define endl            '\n'
#define MAX             2000000000000000
#define MAXN            100000


class Solution {
public:

    ll solve(vector<ll> &vi1, vector<ll> &vi2, vector<ll> &vi3){
        if(vi1[1]!=vi2[1] && vi1[1]!=vi3[1] && vi2[1]!=vi3[1] &&
            vi1[2]!=vi2[2] && vi1[2]!=vi3[2] && vi2[2]!=vi3[2]){
                return vi1[0]+vi2[0]+vi3[0];
            }
        return -MAX;
    }

    ll get(vector<vector<ll> > &vi1, vector<vector<ll> > &vi2, vector<vector<ll> > &vi3){
        ll i,j,k,mx=-MAX;
        for(i=0;i<3;i++){
            for(j=0;j<3;j++){
                for(k=0;k<3;k++){
                    mx=max(mx,solve(vi1[i], vi2[j], vi3[k]));
                }
            }
        }
        return mx;
    }

    long long maximumValueSum(vector<vector<int>>& vi) {
        ll n=vi.size(),ans=-MAX,i,j,k,mx=-MAX;
        ll m=vi[0].size();
        vector<vector<vector<ll> > > vi2;
        for(j=0;j<m;j++){
            vector<vector<ll> > vi1;
            for(i=0;i<n;i++){
                vi1.pb({vi[i][j],i,j});
            }
            sort(vi1.rbegin(),vi1.rend());
            vi1.resize(3);
            vi2.pb(vi1);
        }
        for(i=0;i<m;i++){
            for(j=i+1;j<m;j++){
                for(k=j+1;k<m;k++){
                    ans = get(vi2[i], vi2[j], vi2[k]);
                    mx=max(mx,ans);
                }
            }
        }
        return mx;
    }  
};
