#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        ll m=b.size(), n=b[0].size(), ans=INT64_MIN;
        vector<vector<ll>> v;
        for(int i=0;i<m;i++) for(int j=0;j<n;j++) v.push_back({b[i][j], i, j});
        sort(v.begin(), v.end());

        vector<vector<ll>> vm, vi;
        for(int i=0;i<m;i++){
            vector<vector<ll>> temp;
            for(int j=0;j<n;j++) temp.push_back({b[i][j], j});
            sort(temp.begin(), temp.end());
            vm.push_back({temp[n-1][0], temp[n-2][0], temp[n-3][0]});
            vi.push_back({temp[n-1][1], temp[n-2][1], temp[n-3][1]});
        }

        for(int i=0;i<m;i++) for(int j=0;j<3;j++){
            ll r=i, c=vi[i][j], in=m*n - 1;
            for(;in>=0;in--){
                if(v[in][1]==r || v[in][2]==c) continue;

                ll rr=v[in][1], cc=v[in][2], jj=0;

                for(int in3=in-1;in3>=0;in3--){
                    if(v[in3][1]==r || v[in3][2]==c || v[in3][1]==rr || v[in3][2]==cc) continue;
                    ans=max(ans, b[r][c] + b[rr][cc] + v[in3][0]);
                    break;
                }

                if(vi[rr][jj]==cc || vi[rr][jj]==c){
                    jj++;
                    if(vi[rr][jj]==cc || vi[rr][jj]==c) jj++;
                }
                cc=vi[rr][jj];

                for(int in3=in-1;in3>=0;in3--){
                    if(v[in3][1]==r || v[in3][2]==c || v[in3][1]==rr || v[in3][2]==cc) continue;
                    ans=max(ans, b[r][c] + b[rr][cc] + v[in3][0]);
                    break;
                }

                break;
            }
        }
        return ans;
    }
};
