class Solution {
public:
    long long solve(int a,int b,int c,vector<vector<pair<int,int>>>&v2){
        vector<int>v(2);
        long long m=LLONG_MIN;
        long long curr=0;
        for(int i=0;i<3;i++){
            pair<int,int>p1=v2[a][i];
            v[0]=p1.second;
            curr=p1.first;
            for(int j=0;j<3;j++){
                long long k=curr;
                pair<int,int>p2=v2[b][j];
                if(v[0]!=p2.second){
                    curr+=p2.first;
                    v[1]=p2.second;
                }
                else{
                    continue;
                }
                for(int k=0;k<3;k++){
                    pair<int,int>p3=v2[c][k];
                    if(v[0]!=p3.second && v[1]!=p3.second){
                        curr+=p3.first;
                        m=max(m,curr);
                        curr-=p3.first;
                    }
                    else{
                        continue;
                    }
                }
                curr=k;
            }
        }
        return m;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int m=board.size();
        int n=board[0].size();
        vector<vector<pair<int,int>>>v2;
        for(int i=0;i<m;i++){
            vector<pair<int,int>>v1;
            for(int j=0;j<n;j++){
                pair<int,int>p1;
                p1.first=board[i][j];
                p1.second=j;
                v1.push_back(p1);
            }
            sort(v1.rbegin(),v1.rend());
            vector<pair<int,int>>v3;
            v3.push_back(v1[0]);
            v3.push_back(v1[1]);
            v3.push_back(v1[2]);
            v2.push_back(v3);
        }
        long long ans=LLONG_MIN;
        for(int i=0;i<m;i++){
            for(int j=i+1;j<m;j++){
                for(int k=j+1;k<m;k++){
                    ans=max(ans,solve(i,j,k,v2));
                }
            }
        }
        return ans;
    }
};
