class Solution {
 public long maximumValueSum(int[][] b) {
        int m = b.length, n = b[0].length;
        long ans = Long.MIN_VALUE;
        List<List<int[]>> data = new ArrayList<>();
        PriorityQueue<List<int[]>> dp = new PriorityQueue<>(Comparator.comparingInt(a -> a.get(0)[1]));
        for (int[] a : b) {
            PriorityQueue<int[]> minHeap = new PriorityQueue<>(Comparator.comparingInt(z -> z[1]));
            for (int j = 0; j < a.length; j++) {
                minHeap.offer(new int[] {j, a[j]});
                if (minHeap.size() > 3) {
                    minHeap.poll();
                }
            }
            List<int[]> l = new ArrayList<>();
            while (!minHeap.isEmpty()) {
                l.add(minHeap.poll());
            }
            data.add(l);
        }

        for (int i1 = 0; i1 < m; i1++) {
            for (int j1 = 0; j1 < 3; j1++) {
                int v1 = data.get(i1).get(j1)[1];
                int idx1 = data.get(i1).get(j1)[0];
                for (int i2 = i1 + 1; i2 < m; i2++) {
                    for (int j2 = 0; j2 < 3; j2++) {
                        int v2 = data.get(i2).get(j2)[1];
                        int idx2 = data.get(i2).get(j2)[0];
                        if (idx2 == idx1) {
                            continue;
                        }
                        for (int i3 = i2 + 1; i3 < m; i3++) {
                            for (int j3 = 0; j3 < 3; j3++) {
                                int v3 = data.get(i3).get(j3)[1];
                                int idx3 = data.get(i3).get(j3)[0];
                                if (idx3 == idx2 || idx3 == idx1) {
                                    continue;
                                }
                                long sum = (long)v1 + v2 + v3;
                                ans = Math.max(ans, sum);
                            }
                        }

                    }
                }
            }
        }
        return ans;

    }
}
