type Units [][2]int

func (u Units) Len() int {
    return len(u)
}

func (u Units) Less(i, j int) bool {
    return u[i][1] > u[j][1]
}

func (u Units) Swap(i, j int) {
    u[i], u[j] = u[j], u[i]
}

func maximumValueSum(board [][]int) int64 {
    m, n := len(board), len(board[0])
    a := make([][][2]int, m)
    for i, row := range board {
        a[i] = make([][2]int, n)
        for j, v := range row {
            a[i][j] = [2]int{j, v}
        }
        sort.Sort(Units(a[i]))
    }
    var ans int64 = int64(board[0][0]) + int64(board[1][1]) + int64(board[2][2])
    var temp int64
    var rows [3]int
    var cols [3]int
    for i := 0; i < m; i++ {
        rows[0] = i
        cols[0] = a[i][0][0]
        temp += int64(board[rows[0]][cols[0]])
        for j := 0; j < m; j++ {
            if rows[0] == j {
                continue
            }
            rows[1] = j
            for p := 0; p < n; p++ {
                if a[j][p][0] != cols[0] {
                    cols[1] = a[j][p][0]
                    temp += int64(board[rows[1]][cols[1]])
                    break
                }
            }
            for k := 0; k < m; k++ {
                if rows[0] == k || rows[1] == k {
                    continue
                }
                rows[2] = k
                for p := 0; p < n; p++ {
                    if a[k][p][0] != cols[0] && a[k][p][0] != cols[1] {
                        cols[2] = a[k][p][0]
                        temp += int64(board[rows[2]][cols[2]])
                        break
                    }
                }
                if temp > ans {
                    ans = temp
                }
                temp -= int64(board[rows[2]][cols[2]])
            }
            temp -= int64(board[rows[1]][cols[1]])
        }
        temp -= int64(board[rows[0]][cols[0]])
    }
    return ans
}
