class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& mat) {
        int m=mat.size(),n=mat[0].size();long long ans=-3*(1e9);
        vector<vector<pair<long long,long long>>>dp(m);
        for(int i=0;i<m;i++)
        {
            int a,b,c,ai,bi,ci;
            a=b=c=INT_MIN;
            ai=bi=ci=0;
            for(int j=0;j<n;j++)
            {
                if(a<=mat[i][j])
                {
                    c=b;
                    ci=bi;
                    b=a;
                    bi=ai;
                    ai=j;
                    a=mat[i][j];
                }
                else if(b<=mat[i][j])
                {
                   c=b;
                    ci=bi;
                     bi=j;
                    b=mat[i][j];
                }
                else if(c<=mat[i][j])
                {
                     ci=j;
                    c=mat[i][j];
                }
            }
            dp[i]={{a,ai},{b,bi},{c,ci}};
        }
        //sort(dp.begin(),dp.end(),greater<vector<pair<int,int>>>())
        for(int i=0;i<m;i++)
        {
            for(int j=i+1;j<m;j++)
            {
                 for(int k=j+1;k<m;k++)
                 {
                    for(int ic=0;ic<3;ic++)
                    {
                        for(int jc=0;jc<3;jc++)
                        {
                            for(int kc=0;kc<3;kc++)
                            {
                                if((dp[i][ic].second!=dp[j][jc].second)&& (dp[k][kc].second!=dp[j][jc].second) && (dp[i][ic].second!=dp[k][kc].second))
                                {
                                    ans=max(ans,dp[i][ic].first+dp[j][jc].first+dp[k][kc].first);
                                }
                            }
                        }
                    }
                 }
            }
        }
        return ans;
    }
};
