class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])

        top_three = []

        for col in range(n):
            max_values = []
            for row in range(m):
                max_values.append((board[row][col], row))
            max_values.sort(reverse=True)  
            top_three.append(max_values[:3])  

        max_sum = float('-inf')

        for c1 in range(n):
            for c2 in range(c1 + 1, n):
                for c3 in range(c2 + 1, n):
                    for val1, r1 in top_three[c1]:
                        for val2, r2 in top_three[c2]:
                            if r2 == r1:
                                continue  
                            for val3, r3 in top_three[c3]:
                                if r3 == r1 or r3 == r2:
                                    continue  

                                current_sum = val1 + val2 + val3
                                max_sum = max(max_sum, current_sum)

        return max_sum
