#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        ll n=board.size();
        ll m=board[0].size();
        vector<vector<pair<ll, ll>>> v(n, vector<pair<ll, ll>> (3));
        for(ll i=0; i<n; i++){
            priority_queue<pair<ll, ll>> pq;
            for(ll j=0; j<m; j++){
                pq.push({board[i][j], j});
            }
            v[i][0]=pq.top();
            pq.pop();
            v[i][1]=pq.top();
            pq.pop();
            v[i][2]=pq.top();
        }
        ll ans=-1e18;
        for(ll i=0; i<n; i++){
            for(ll ind1=0; ind1<3; ind1++){
                for(ll j=0; j<n; j++){
                    for(ll ind2=0; ind2<3; ind2++){
                        for(ll k=0; k<n; k++){
                            for(ll ind3=0; ind3<3; ind3++){
                                ll first=v[i][ind1].first;
                                ll first_ind=v[i][ind1].second;
                                ll second=v[j][ind2].first;
                                ll second_ind=v[j][ind2].second;
                                ll third=v[k][ind3].first;
                                ll third_ind=v[k][ind3].second;
                                if(first_ind!=second_ind && first_ind!=third_ind && second_ind!=third_ind && i!=j && j!=k && i!=k){
                                    ans=max(ans, (first+second+third));
                                }
                            }
                        }
                    }

                }
            }
        }
        return ans;
    }
};
