class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m=board.size();
        int n=board[0].size();
        map<int,vector<int>>colmax;
        for(int i=0;i<n;i++){
            vector<pair<int,int>>col;
            for(int j=0;j<m;j++){
                col.push_back({board[j][i],j});
            }
            sort(col.rbegin(),col.rend());
            colmax[i].push_back(col[0].second);
            colmax[i].push_back(col[1].second);
            colmax[i].push_back(col[2].second);
            // cout<<col[0].second;
        }

//         3 cols
        // cout<<board[0][0]+<<endl;
        long long maxi=-1e18;
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    vector<int>a=colmax[i];
                    vector<int>b=colmax[j];
                    vector<int>c=colmax[k];
                    for(int x=0;x<3;x++){
                        for(int y=0;y<3;y++){
                            for(int z=0;z<3;z++){
                                if(a[x]!=b[y] && a[x]!=c[z] && b[y]!=c[z]){
                                    // cout<<x<<" "<<y<<" "<<z<<endl;
                                    // cout<<board[x][i]+board[y][j]+board[z][k]<<endl;
                                    maxi=max(maxi,1ll*board[a[x]][i]+board[b[y]][j]+board[c[z]][k]);
                                }
                            }
                        }
                    }
                }
            }
        }
        return maxi;
    }
};
