class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int m=a.size();
        int n=a[0].size();
        map<int,vector<array<long long,2>>> m1;
        for(int i=0;i<m;i++)
        {
            for(int j=0;j<n;j++)
                m1[i].push_back({a[i][j],j});
            sort(m1[i].begin(),m1[i].end(),greater<array<long long,2>>());
        }
        vector<vector<array<long long,2>>> mx(m);
        for(int i=0;i<m;i++)
        {
            int c=0;
            for(auto &x:m1[i])
            {
                if(c==3)
                    break;
                c++;
                mx[i].push_back(x);
            }
        }
        // for(int i=0;i<m;i++)
        // {
        //     for(auto &x:mx[i])
        //         cout<<x[0]<<","<<x[1]<<" ";
        //     cout<<"\n";
        // }
        long long ans=-1e18;
        for(int i=0;i<m;i++)
        {
            for(int j=i+1;j<m;j++)
            {
                for(int k=j+1;k<m;k++)
                {
                    for(int ii=0;ii<3;ii++)
                    {
                        for(int jj=0;jj<3;jj++)
                        {
                            if(mx[i][ii][1]==mx[j][jj][1])
                                continue;
                            for(int kk=0;kk<3;kk++)
                            {
                                if(mx[i][ii][1]==mx[k][kk][1] || mx[j][jj][1]==mx[k][kk][1])
                                    continue;
                                ans=max(ans,mx[i][ii][0]+mx[j][jj][0]+mx[k][kk][0]);
                            }

                        }
                    }
                }
            }
        }
        return ans;
    }
};
