class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size(); 
        long long ans = -1e18;
        vector<vector<vector<long long>>> dp(n + 1, vector<vector<long long>>(m + 1, vector<
        long long>(m + 1, -1e18))); 
        for(int j = 0; j < m; ++ j ) dp[0][j][j] = a[0][j];  
        for(int i = 1; i < n; ++ i ) {
            for(int j = 0; j < m; ++ j ) 
                for(int k = j; k < m; ++ k ) {
                    if(j==k) dp[i][j][j] = max((long long)a[i][j], dp[i-1][j][j]); 
                    else {

                        dp[i][j][k] = max(dp[i][j][k], dp[i-1][j][k]); 
                        if(dp[i-1][j][j]!=-1e18) dp[i][j][k] = max(dp[i][j][k], dp[i-1][j][j] + a[i][k]); 
                        if(dp[i-1][k][k]!=-1e18) dp[i][j][k] = max(dp[i][j][k], dp[i-1][k][k] + a[i][j]); 
                    }
                }
        }
        for(int i = 2; i < n; ++ i ) {
            for(int j = 0; j < m; ++ j ) 
                for(int k = j + 1; k < m; ++ k ) {
                    int u = -1e9;
                    for(int c = 0; c < m; ++ c )
                        if(c!=j and c!=k)u=max(u,a[i][c]); 
                    ans = max(ans, dp[i - 1][j][k] + u);
                }
        }


        return ans; 
    }
};
