class Solution {
public:
    bool static cmp(const pair<int,int>&a,const pair<int,int>&b)
    {
        return a.first > b.first;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<int>col[n];
        long long int ans = -3e9;
        for(int i=0;i<n;i++)
        {
            vector<pair<int,int>>v;
            for(int j=0;j<m;j++)
            v.push_back({board[i][j],j});
            sort(v.begin(),v.end(),cmp);
            // for(int j=0;j<v.size();j++)
            // cout<<v[j].first<<" "<<v[j].second<<"\n";
            for(int j=0;j<min(4,m);j++)
            col[i].push_back(v[j].second);
        }

         for(int i=0;i<=n-3;i++)
        {
            for(int j=i+1;j<=n-2;j++)
            {
                for(int k=j+1;k<=n-1;k++)
                {
                    // i,j,k are rows
                    // vector<int>v1 = col[i];
                    // vector<int
                    // for(int p=0;p<col[i].size();p++)
                    // cout<<board[i][col[i][p]]<<" ";
                    int s1 = col[i].size();
                    int s2 = col[j].size();
                    int s3 = col[k].size();
                    for(int a=0;a<s1;a++)
                    {
                        for(int b=0;b<s2;b++)
                        {
                            for(int c=0;c<s3;c++)
                            {
                                if((col[i][a] != col[j][b]) && (col[i][a] != col[k][c]) && (col[k][c] != col[j][b]))
                                {

                                    long long int s = board[i][col[i][a]];
                                    s += board[j][col[j][b]];
                                    s += board[k][col[k][c]];
                                    if(ans < s)
                                    ans = s;
                                    // //if(s == 1000000002)
                                    // cout<<col[i][a]<<" "<<col[j][b]<<" "<<col[k][c]<<" "<<s<<"\n";
                                }
                            }
                        }
                    }

                }
            }
        }
       return ans;

    }
};
