class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        rows=len(board)
        cols=len(board[0])

        upDown=[[0 for j in range(cols)] for i in range(rows)]
        upDown[0]=board[0]
        for i in range(1,rows):
            for j in range(cols):
                upDown[i][j]=max(board[i][j],upDown[i-1][j])

        downUp=[[0 for j in range(cols)] for i in range(rows)]
        downUp[-1]=board[-1]
        for i in range(rows-2,-1,-1):
            for j in range(cols):
                downUp[i][j]=max(board[i][j],downUp[i+1][j])

        ans=-sys.maxsize
        for i in range(1,rows-1):
            for j in range(cols):
                up = [[upDown[i-1][k],k] for k in range(cols) if k!=j]
                down = [[downUp[i+1][k],k] for k in range(cols) if k!=j]

                up.sort(reverse=True)
                down.sort(reverse=True)

                if up[0][1]!=down[0][1]:
                    ans=max(ans,up[0][0]+down[0][0]+board[i][j])
                else:
                    ans=max(ans,up[0][0]+down[1][0]+board[i][j],up[1][0]+down[0][0]+board[i][j])

        return ans
