class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();


        vector<vector<int>> row_top3;
        for (auto &r : board) {
            vector<int> top3;
            for (int i = 0; i < n; i++) {
                if (top3.size() < 3) {
                    top3.push_back(i);
                } else if (r[top3.back()] < r[i]) {
                    top3[2] = i;
                } else {
                    continue;
                }

                for (int j = top3.size() - 1; j > 0; j--) {
                    if (r[top3[j - 1]] < r[top3[j]]) {
                        int t = top3[j];
                        top3[j] = top3[j - 1];
                        top3[j - 1] = t;
                    }
                }
            }
            row_top3.push_back(top3);

            // for (int t : top3) {
            //     cout << t << "  ";
            // }cout << endl;
        }

        auto get = [&](int i1, int i2, int i3, vector<int> &t1, vector<int> &t2, vector<int> &t3) {
            long long mx = 3ll * INT_MIN;
            for (int j1 = 0; j1 < 3; j1++) {
                for (int j2 = 0; j2 < 3; j2++) {
                    for (int j3 = 0; j3 < 3; j3++) {
                        if (t1[j1] != t2[j2] && t1[j1] != t3[j3] && t2[j2] != t3[j3]) {
                            //cout << t1[j1] << " " << t2[j2] << " " << t3[j3] << endl;
                            mx = max(mx, board[i1][t1[j1]] * 1ll + board[i2][t2[j2]] + board[i3][t3[j3]]);
                        }
                    }
                }
            }
            return mx;
        };

        long long ans = 3ll * INT_MIN;
        for (int i = 0; i < m; i++) {
            for (int j = i + 1; j < m; j++) {
                for (int k = j + 1; k < m; k++) {
                    ans = max(ans, get(i, j, k, row_top3[i], row_top3[j], row_top3[k]));
                }
            }
        }
        return ans;
    }
};
